//pasamos todo a minusculas 

string = string.toLowerCase()

// split a la cadena en los espacios

let arr = string.split(" ")

//creamos un arreglo para los resultados finales

let res = []
let strings = []
//agregamos la primera palabra a las respuestas
res.push({
    string: arr[0],
    cont:1
})
strings.push(arr[0])

//recorremos todas las palabras de la cadena original
for (let i = 1; i < arr.length; i++){
    if (!strings.includes(arr[i])){
        strings.push(arr[i])
        res.push({
            string: arr[i],
            cont:1
        })
    }
    else{
        for (let j = 0; j<res.length; j++){
            if (res[j].string == arr[i]){
                res[j].cont += 1
            }
        }
    }
    
}


// imprimir resultado
let s = ""
for (let i=0; i<res.length ; i++){
    s += res[i].string + res[i].cont
}
